


public class StringFunction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String name="Sagar Kumar";
		System.out.println(name);
		System.out.println(name.length());
		System.out.println(name.toLowerCase());
		System.out.println(name.toUpperCase());
		System.out.println(name.trim());
		System.out.println(name.endsWith("ar"));
		System.out.println(name.startsWith(" "));
		System.out.println( name.charAt(9));
		System.out.println(name.indexOf("ma"));
		System.out.println(name.equals("Sagar"));
		System.out.println(name.equals("Sagar Kumar"));
		System.out.println(name.equalsIgnoreCase("sagar KUMAR"));
		System.out.println("I am escape sequence \" double quote");

	}

}
